package hashing;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map.Entry;

public class FindAllAnagramsInAString {

	
	public static boolean compareSets(HashMap<Character, Integer> pSet, String s) {
		
		HashMap<Character, Integer> sSet = new HashMap<Character, Integer>();
		
		for(int i=0; i<s.length(); i++) {
			if(sSet.containsKey(s.charAt(i))) {
				sSet.put(s.charAt(i), sSet.get(s.charAt(i))+1);
			}
			else {
				sSet.put(s.charAt(i), 1);
			}
		}
		
		if(pSet.size() != sSet.size())return false;
		
		for(Entry<Character, Integer> e: pSet.entrySet()) {
			int a = 0, b =0;
			if(pSet.get(e.getKey()) != null) {
				a = pSet.get(e.getKey());
			}
			
			if(sSet.get(e.getKey()) != null) {
				b = sSet.get(e.getKey());
			}
			if(a != b) {
				return false;
			}
		}
		
		return true;
		
	}
	
	
	public static List<Integer> findingAnagrams(String s, String p){
		
		HashMap<Character, Integer> set = new HashMap<Character, Integer>();
		ArrayList<Integer> resultArrayList = new ArrayList<Integer>();
		for(int i=0; i<p.length(); i++) {
			if(set.containsKey(p.charAt(i))) {
				set.put(p.charAt(i), set.get(p.charAt(i))+1);
			}
			else {
				set.put(p.charAt(i), 1);
			}
		}
		
		for(int i=0; i<=s.length()-p.length(); i++) {
			String temp = s.substring(i, i+p.length());
			if(compareSets(set, temp)) {
				resultArrayList.add(i);
			}
			
		}

		return resultArrayList;
		
	}
	
	//Fast Solution
	public static void fastMethod(String s, String p) {
		List<Integer> indexes = new ArrayList<>();

	    HashMap<Character,Integer> pMap = new HashMap<>();
	    // this will be static map which will be set only once
	    for(char c : p.toCharArray()){
	        pMap.put(c, pMap.getOrDefault(c,0)+1);
	    }
	    HashMap<Character,Integer> sMap = new HashMap<>();
	    int windowStart = 0;
	    for(int windowEnd = 0; windowEnd<s.length();windowEnd++){
	        char c = s.charAt(windowEnd);
	        // add to second map
	        sMap.put(c, sMap.getOrDefault(c, 0)+1);
	        // check if total iteration are equal to number of character in string S1, so we can start check for permutation
	        if(windowEnd+1>=p.length()){
	            // if both maps are equlas, add the windowStart index in the list to be returned
	            if(sMap.equals(pMap)){
	                indexes.add(windowStart);
	            }
	            // If not equal then subtract / remove the leftest character from sMap
	            char leftChar = s.charAt(windowStart);
	            sMap.put(leftChar, sMap.get(leftChar)-1);
	            if(sMap.get(leftChar)==0){
	                sMap.remove(leftChar,0);
	            }
	            windowStart++;

	        }
	    }
	    System.out.println(indexes);
	}
	
	
	public static void main(String[] args) {
		String s = "";		
		String p = "";
		System.out.println(findingAnagrams(s, p));
	}

}
